package main

import (
	"encoding/xml"
	"flag"
	"fmt"
	"io/ioutil"
	"log"
	"os"
	"regexp"

	"net/http"

	"github.com/gocarina/gocsv"
)

func readURL(u string) ([]byte, error) {
	var bodyBytes []byte
	// Generated by curl-to-Go: https://mholt.github.io/curl-to-go
	resp, err := http.Get(u)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	//https://stackoverflow.com/a/38673698/2777965
	if resp.StatusCode == http.StatusOK {
		bodyBytes, err = ioutil.ReadAll(resp.Body)
		if err != nil {
			return nil, err
		}
	} else {
		return nil, fmt.Errorf("StatusCode not 200, but '%v'", resp.StatusCode)
	}
	return bodyBytes, nil
}

type feed struct {
	Feed  xml.Name `xml:"feed"`
	Entry []Entry  `xml:"entry"`
}

type Entry struct {
	XMLName   xml.Name `xml:"entry"`
	Published string   `xml:"published"`
	Title     string   `xml:"title"`
}

func atom(dateStr string, u string) error {
	b, err := readURL(u)
	if err != nil {
		return err
	}

	var f feed
	xml.Unmarshal(b, &f)

	for i := 0; i < len(f.Entry); i++ {
		pubDate := f.Entry[i].Published
		match, err := regexp.MatchString(dateStr, pubDate)
		if err != nil {
			return err
		}
		if match {
			fmt.Println(pubDate + " " + f.Entry[i].Title)
		}
	}
	return nil
}

type RSSFeeds struct {
	Type string `csv:"type"`
	URL  string `csv:"url"`
}

func csv(f string) ([]*RSSFeeds, error) {
	u, err := os.OpenFile(f, os.O_RDWR|os.O_CREATE, os.ModePerm)
	if err != nil {
		return nil, err
	}
	defer u.Close()

	r := []*RSSFeeds{}

	if err := gocsv.UnmarshalFile(u, &r); err != nil {
		return nil, err
	}
	return r, err
}

func main() {
	d := flag.String("date", "^2020-05-16.*", "Get the RSS feeds from a certain date")
	f := flag.String("file", "informado.csv", "The file that contains a list of RSS URLs")
	flag.Parse()
	urls, err := csv(*f)
	if err != nil {
		log.Fatal(err)
	}
	for _, u := range urls {
		switch t := u.Type; t {
		case "atom":
			atom(*d, u.URL)
		default:
			fmt.Printf("Unsupported type '%v'.\n", t)
		}
	}
}
